import './App.css';
import Navigation from './componenets/navigation/Navigation';
import Logo from './componenets/logo/Logo';
import Rank from './componenets/rank/rank'
import ImageLink from './componenets/imageLink/imageLink';
import Particles from 'react-particles-js';
import { Component } from 'react';
import FaceReco from './componenets/face';
import Clarifai from 'clarifai';


const app = new Clarifai.App({ apiKey: '8574ae4ec5094e459de26dbf89fe7742' });

class App extends Component {

  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      bound: {}
    }
  }

  calculateFaceLocation = (data) => {

    var faces = [data];

    faces.forEach(function (i) {
      console.log(i);
      const image = document.getElementById('inputimage');
      const width = Number(image.width);
      const height = Number(image.height);
      return {

        leftcol: i.left_col * width,
        topRow: i.top_row * height,
        rightcol: width - (i.right_col * width),
        bottomrow: height - (i.bottom_row * height),

      }
    })


  }

  displayFaceBox = (box) => {
    //console.log(box);
    this.setState({ bound: box })
  }

  onInputChange = (event) => {
    this.setState({ input: event.target.value })
  }

  onButtonSubmit = () => {
    this.setState({ imageUrl: this.state.input })

    app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
      .then(response =>
        this.displayFaceBox(
          this.calculateFaceLocation(
            response.outputs[0].data.regions)))
      .catch((err => {
        console.log(err);
      }))

  }

  render() {

    return (
      <div className="App" >

        <Particles className='particles' />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLink onInputChange={this.onInputChange} onSubmit={this.onButtonSubmit} />
        <FaceReco imageUrl={this.state.imageUrl} bound={this.state.bound} />

      </div>
    );
  }
}

export default App;
